/**
 * Pan the map so that the overlay is entirely visible in the current viewport
 * (if necessary).
 * @protected
 */
_ol_Overlay_.prototype.panIntoView = function() {
  var map = this.getMap();

  if (!map || !map.getTargetElement()) {
    return;
  }

  var mapRect = this.getRect(map.getTargetElement(), map.getSize());
  var element = /** @type {!Element} */ (this.getElement());
  var overlayRect = this.getRect(element,
      [_ol_dom_.outerWidth(element), _ol_dom_.outerHeight(element)]);

  var margin = this.autoPanMargin;
  if (!_ol_extent_.containsExtent(mapRect, overlayRect)) {
    // the overlay is not completely inside the viewport, so pan the map
    var offsetLeft = overlayRect[0] - mapRect[0];
    var offsetRight = mapRect[2] - overlayRect[2];
    var offsetTop = overlayRect[1] - mapRect[1];
    var offsetBottom = mapRect[3] - overlayRect[3];

    var delta = [0, 0];
    if (offsetLeft < 0) {
      // move map to the left
      delta[0] = offsetLeft - margin;
    } else if (offsetRight < 0) {
      // move map to the right
      delta[0] = Math.abs(offsetRight) + margin;
    }
    if (offsetTop < 0) {
      // move map up
      delta[1] = offsetTop - margin;
    } else if (offsetBottom < 0) {
      // move map down
      delta[1] = Math.abs(offsetBottom) + margin;
    }

    if (delta[0] !== 0 || delta[1] !== 0) {
      var center = /** @type {ol.Coordinate} */ (map.getView().getCenter());
      var centerPx = map.getPixelFromCoordinate(center);
      var newCenterPx = [
        centerPx[0] + delta[0],
        centerPx[1] + delta[1]
      ];

      map.getView().animate({
        center: map.getCoordinateFromPixel(newCenterPx),
        duration: this.autoPanAnimation.duration,
        easing: this.autoPanAnimation.easing
      });
    }
  }
};


/**
 * Get the extent of an element relative to the document
 * @param {Element|undefined} element The element.
 * @param {ol.Size|undefined} size The size of the element.
 * @return {ol.Extent} The extent.
 * @protected
 */
_ol_Overlay_.prototype.getRect = function(element, size) {
  var box = element.getBoundingClientRect();
  var offsetX = box.left + window.pageXOffset;
  var offsetY = box.top + window.pageYOffset;
  return [
    offsetX,
    offsetY,
    offsetX + size[0],
    offsetY + size[1]
  ];
};


/**
 * Set the positioning for this overlay.
 * @param {ol.OverlayPositioning} positioning how the overlay is
 *     positioned relative to its point on the map.
 * @observable
 * @api
 */
_ol_Overlay_.prototype.setPositioning = function(positioning) {
  this.set(_ol_Overlay_.Property.POSITIONING, positioning);
};


/**
 * Modify the visibility of the element.
 * @param {boolean} visible Element visibility.
 * @protected
 */
_ol_Overlay_.prototype.setVisible = function(visible) {
  if (this.rendered.visible !== visible) {
    this.element.style.display = visible ? '' : 'none';
    this.rendered.visible = visible;
  }
};


/**
 * Update pixel position.
 * @protected
 */
_ol_Overlay_.prototype.updatePixelPosition = function() {
  var map = this.getMap();
  var position = this.getPosition();
  if (!map || !map.isRendered() || !position) {
    this.setVisible(false);
    return;
  }

  var pixel = map.getPixelFromCoordinate(position);
  var mapSize = map.getSize();
  this.updateRenderedPosition(pixel, mapSize);
};


/**
 * @param {ol.Pixel} pixel The pixel location.
 * @param {ol.Size|undefined} mapSize The map size.
 * @protected
 */
_ol_Overlay_.prototype.updateRenderedPosition = function(pixel, mapSize) {
  var style = this.element.style;
  var offset = this.getOffset();

  var positioning = this.getPositioning();

  this.setVisible(true);

  var offsetX = offset[0];
  var offsetY = offset[1];
  if (positioning == _ol_OverlayPositioning_.BOTTOM_RIGHT ||
      positioning == _ol_OverlayPositioning_.CENTER_RIGHT ||
      positioning == _ol_OverlayPositioning_.TOP_RIGHT) {
    if (this.rendered.left_ !== '') {
      this.rendered.left_ = style.left = '';
    }
    var right = Math.round(mapSize[0] - pixel[0] - offsetX) + 'px';
    if (this.rendered.right_ != right) {
      this.rendered.right_ = style.right = right;
    }
  } else {
    if (this.rendered.right_ !== '') {
      this.rendered.right_ = style.right = '';
    }
    if (positioning == _ol_OverlayPositioning_.BOTTOM_CENTER ||
        positioning == _ol_OverlayPositioning_.CENTER_CENTER ||
        positioning == _ol_OverlayPositioning_.TOP_CENTER) {
      offsetX -= this.element.offsetWidth / 2;
    }
    var left = Math.round(pixel[0] + offsetX) + 'px';
    if (this.rendered.left_ != left) {
      this.rendered.left_ = style.left = left;
    }
  }
  if (positioning == _ol_OverlayPositioning_.BOTTOM_LEFT ||
      positioning == _ol_OverlayPositioning_.BOTTOM_CENTER ||
      positioning == _ol_OverlayPositioning_.BOTTOM_RIGHT) {
    if (this.rendered.top_ !== '') {
      this.rendered.top_ = style.top = '';
    }
    var bottom = Math.round(mapSize[1] - pixel[1] - offsetY) + 'px';
    if (this.rendered.bottom_ != bottom) {
      this.rendered.bottom_ = style.bottom = bottom;
    }
  } else {
    if (this.rendered.bottom_ !== '') {
      this.rendered.bottom_ = style.bottom = '';
    }
    if (positioning == _ol_OverlayPositioning_.CENTER_LEFT ||
        positioning == _ol_OverlayPositioning_.CENTER_CENTER ||
        positioning == _ol_OverlayPositioning_.CENTER_RIGHT) {
      offsetY -= this.element.offsetHeight / 2;
    }
    var top = Math.round(pixel[1] + offsetY) + 'px';
    if (this.rendered.top_ != top) {
      this.rendered.top_ = style.top = top;
    }
  }
};


/**
 * returns the options this Overlay has been created with
 * @public
 * @return {olx.OverlayOptions} overlay options
 */
_ol_Overlay_.prototype.getOptions = function() {
  return this.options;
};


/**
 * @enum {string}
 * @protected
 */
_ol_Overlay_.Property = {
  ELEMENT: 'element',
  MAP: 'map',
  OFFSET: 'offset',
  POSITION: 'position',
  POSITIONING: 'positioning'
};

class ServiceOpenLayers {

    /**
     * Create a ServiceOpenLayers object.
     *
     * @param 	{object} 	j_query
     * @param 	{array} 	map_data
     * @param 	{string} 	invalid_address
     * @param 	{array} 	user_markers
     */
    constructor(j_query, invalid_address, map_data, user_markers) {
        this.j_query = j_query;
        this.invalid_address = invalid_address;
        this.map_data = map_data;
        this.user_markers = user_markers;
        this.views = [];
        this.map = null;
    }

    /**
     * Initialize values
     *
     * @param {object} ol_map_data
     * @return {void}
     */
    init(ol_map_data) {
        for (let id in ol_map_data) {
            let map = ol_map_data[id];
			let zoom = map[2];
			let central_marker = map[3];
			let replace_marker = map[5];
			let geo = map[7];
			let pos = this.posToOSM([map[1], map[0]]);

			this.geolocationURL = "http://"+ geo;
			this.initView(id, pos, zoom);
			this.initMap(id, replace_marker);
			this.initUserMarkers(id, pos, replace_marker, central_marker);
        }
    }

    /**
     * Initialize the view array.
     *
     * @param {string} id
     * @param {array} pos
     * @param {number} zoom
     * @return {void}
     */
    initView(id, pos, zoom) {
        this.views[id] = new _ol_View_();
		this.views[id].setCenter(pos);
		this.views[id].setMaxZoom(18);
		this.views[id].setMinZoom(0);
		this.views[id].setZoom(zoom);

		// Bind the maps zoom level to the select box zoom.
		this.views[id].on("propertychange", function(e) {
			if(e.key === 'resolution') {
				$("#" + id + "_zoom").val(Math.floor(this.views[id].getZoom()));
			}
		}, this);
    }

    /**
     * Initialise a map on the page.
     *
     * @param 	{string} 	id
     * @param 	{boolean} 	replace_marker
     * @return 	{void}
     */
    initMap(id, replace_marker) {
        this.map = new _ol_Map_({
            layers: [
                new _ol_layer_Tile_({
                    preload: 4,
                    source: new _ol_source_OSM_()
                }),
            ],
            target: id,
            controls: new _ol_control_.defaults().extend([
                new _ol_control_FullScreen_()
            ]),
            loadTilesWhileAnimating: true,
            view: this.views[id]
        });

        this.map.on("click", function(e) {
            e.preventDefault();
            let center = e.coordinate;
            this.jumpTo(id, center);
            if (replace_marker) {
                this.deleteAllMarkers(id);
                this.setMarker(id, center);
            }
            this.updateInputFields(id, center);
        }, this);
    }

    /**
     * Init the user_markers array.
     *
     * @param 	{string} 	id
     * @param 	{number} 	pos
     * @param 	{boolean} 	replace_marker
     * @param 	{boolean} 	central_marker
     * @returns {void}
     */
    initUserMarkers(id, pos, replace_marker, central_marker) {
        if(replace_marker || central_marker) {
            this.deleteAllMarkers(id);
            this.setMarker(id, pos);
            return;
        }

        // Only for participants overview.
        // Navigation is managed by participants-buttons here.
        this.map.removeEventListener('click');
        let mapUserMarkers = this.user_markers[id];
        for (let cnt in mapUserMarkers) {
            let userMarkerData = mapUserMarkers[cnt];
            let pos = this.posToOSM([userMarkerData[0], userMarkerData[1]]);
            this.user_markers[cnt] = [pos, userMarkerData[2]];
            this.setMarker(id, pos, userMarkerData[2]);
        }
    }

    /**
     * Transform a coordinate from OSM projection to human readable projection.
     *
     * @param 	{array} pos 	[longitude, latitude]
     * @return 	{array} 		[longitude, latitude]
     */
    posToHuman(pos) {
        return _ol_proj_.transform(pos, "EPSG:3857", "EPSG:4326")
    }

    /**
     * Transform a coordinate from human readable projection to OSM projection.
     *
     * @param 	{array} pos 	[longitude, latitude]
     * @return 	{array} 		[longitude, latitude]
     */
    posToOSM(pos) {
        return _ol_proj_.transform(pos, "EPSG:4326", "EPSG:3857");
    }

    /**
     * Jump to a position on the map.
     *
     * @param 	{string} 	id
     * @param 	{array} 	pos 	[longitude, latitude]
     * @param 	{number} 	zoom
     * @return 	{void}
     */
    jumpTo(id, pos, zoom) {
        this.views[id].animate({
            center: pos,
            duration: 2000,
            zoom: zoom
        });
    }

    /**
     * Looks up for an user given Address.
     *
     * @param 	{string} id
     * @param 	{string} address
     * @return 	{void}
     */
    jumpToAddress(id, address) {
        $("#" + id + "_addr").attr("disabled", "disabled");
        $("#" + id + "_lng").attr("disabled", "disabled");
        $("#" + id + "_lat").attr("disabled", "disabled");

        $.ajax({
            url: this.geolocationURL.replace("[QUERY]", address),
            data: {},
            dataType: "json"
        }).done((function(module) {
            return function (data) {
                if (data.length === 0) {
                    $("#" + id + "_addr").val(module.addressInvalid);
                    return;
                }
                let lon = parseFloat(data[0].lon, 10);
                let lat = parseFloat(data[0].lat, 10);

                let pos = module.posToOSM([lon, lat]);

                module.jumpTo(id, pos, 16);
                module.deleteAllMarkers(id);
                module.setMarker(id, pos);
                module.updateInputFields(id, pos, address);
            };
        })(this))
            .fail(function() {
                $("#" + id + "_address").val("");
            })
            .always( function() {
                $("#" + id + "_addr").removeAttr("disabled");
                $("#" + id + "_lng").removeAttr("disabled");
                $("#" + id + "_lat").removeAttr("disabled");
            });
    }

    /**
     * Force throwing a resize event.
     *
     * @return 	{void}
     */
    forceResize() {
        $('input[onclick*="il.Form.showSubForm"]').each(function() {
            let e = $(this);
            e.attr(
                'onclick',
                e.attr('onclick') + ";window.dispatchEvent(new Event('resize'));");
        });
    }

    /**
     * Set a marker at the given position at the map.
     *
     * @param 	{string} 	id
     * @param 	{array} 	pos 	[longitude, latitude]
     * @return 	{void}
     */
    setMarker(id, pos) {
        let container = document.getElementById(id);
        let element = document.createElement("div");
        element.className = "marker";
        container.appendChild(element);
        let marker = new _ol_Overlay_({
            element: element
        });
        marker.setOffset([-7.5, -23.5]);
        marker.setPosition(pos);
        this.map.addOverlay(marker);

        element.innerHTML = "<img src='./Services/Maps/images/mm_20_blue.png'>";
    }

    /**
     * Remove all child elements.
     *
     * @param {string} 	id
     * @returns {void}
     */
    deleteAllMarkers(id) {
        let marker = document.getElementById(id).querySelectorAll('.marker');
        for (var i = 0; i < marker.length; i++) {
            marker[i].remove();
        }
    }

    /**
     * Move to a user marker and open popup.
     *
     * @param 	{string} id
     * @param 	{number} j 	Counter for user_markers.
     * @returns {void}
     */
    moveToUserMarkerAndOpen(id, j) {
        let user_marker = this.user_markers[j];
        if (user_marker) {
            this.deleteAllPopups();
            this.jumpTo(id, user_marker[0], 16);
            this.setPopup(id, user_marker[0], user_marker[1]);
        }
        else {
            console.log("No user marker no. "+j+" for map "+id);
        }
    }

    /**
     * Set a popup window to pos.
     *
     * @param 	{string} 	id
     * @param 	{array} 	pos
     * @param 	{string} 	elem 	Can hold html or pure text.
     * @returns {void}
     */
    setPopup(id, pos, elem) {
        let container = document.getElementById(id);
        let append = document.createElement("div");
        append.className = "arrow_box";
        append.addEventListener('click', (function(module) {
            return function() {
                module.deleteAllPopups();
            }
        })(this));
        append.innerHTML = elem;
        container.appendChild(append);

        let popup = new this.ol.Overlay({
            element: append,
            insertFirst: false
        });
        popup.setOffset([15.5, -53.5]);
        popup.setPosition(pos);
        this.map.addOverlay(popup);
    }

    /**
     * Delete all popups with class arrow_box.
     *
     * @returns 	{void}
     */
    deleteAllPopups() {
        let popups = document.getElementsByClassName('arrow_box');
        for (var i = 0; i < popups.length; i++) {
            popups[i].remove();
        }
    }

    /**
     * Update the longitude, latitude and the zoom of the map.
     *
     * @param 	{string} id
     * @return 	{void}
     */
    updateMap(id) {
        let lat = parseFloat($("#" + id + "_lat").val());
        let lon = parseFloat($("#" + id + "_lng").val());
        let zoom = $("#"+id+"_zoom").val();
        let pos = this.posToOSM([lon, lat]);

        //this.updateMarkers(id);
        this.views[id].setZoom(zoom);
        this.jumpTo(id, pos);
        this.updateInputFields(id, pos);
    }

    /**
     * Update the input fields.
     *
     * @param 	{string} 	id
     * @param 	{array} 	pos 	[longitude, latitude]
     * @param 	{string} 	address
     * @return 	{void}
     */
    updateInputFields(id, pos, address) {
        address = address || "undefined";
        let human_pos = this.posToHuman(pos);
        $("#" + id + "_addr").val(address);
        $("#" + id + "_lng").val(human_pos[0]);
        $("#" + id + "_lat").val(human_pos[1]);
    }
}

// For passing data from ilias to js
let ilOLMapData = [];
let ilOLUserMarkers = [];
let ilOLInvalidAddress = undefined;

if (jQuery && il.Util.addOnLoad) {
    il.Util.addOnLoad(function() {
        let openLayer = new ServiceOpenLayers(jQuery, ilOLInvalidAddress, ilOLMapData, ilOLUserMarkers);

        ilLookupAddress = function(id, address) {
            return openLayer.jumpToAddress(id, address);
        };

        ilUpdateMap = function (id) {
            return openLayer.updateMap(id);
        };

        ilShowUserMarker = function(id, counter) {
            return openLayer.moveToUserMarkerAndOpen(id, counter);
        };

        openLayer.forceResize(jQuery);
        openLayer.init(ilOLMapData);
    });
}
