INSERT INTO `lng_data` VALUES ('common','webfolder_instructions_info','en','The webfolder instructions are shown on browsers, which can not open a webfolder directly. You can use HTML code, and the following placeholders: <b>[WEBFOLDER_TITLE]</b>, <b>[WEBFOLDER_URI]</b>, <b>[WEBFOLDER_URI_IE]</b>, <b>[WEBFOLDER_URI_KONQUEROR]</b>, <b>[WEBFOLDER_URI_NAUTILUS]</b>, <b>[ADMIN_MAIL]</b>, <b>[IF_WINDOWS]</b>...<b>[/IF_WINDOWS]</b>, <b>[IF_MAC]</b>...<b>[/IF_MAC]</b>, <b>[IF_LINUX]</b>...<b>[/IF_LINUX]</b>. Clear the field to get the default instructions.');
INSERT INTO `lng_data` VALUES ('common','webfolder_instructions_titletext','en','Open %1$s as webfolder');
INSERT INTO `lng_data` VALUES ('common','webfolder_instructions_text','en','<p>Follow the instructions below, to open <b>[WEBFOLDER_TITLE]</b> as a webfolder:</p>[IF_WINDOWS]<h3>Instructions for Windows Vista</h3><ol><li>Press the key combination Windows+E to open Windows Explorer.</li><li>Click on \'Add Network Place\' in the toolbar. The \'Add a Network Place\' wizard will open.</li><li>Click at the link \'Connect to a website\'.</li><li>Select the \'Choose Another Network Location\' option, and select \'Next\'.<li>In the \'Internet or Network Address\' field enter<br/><b>[WEBFOLDER_URI]</b><br/>then select \'Next\'.</li><li>Enter your login and password, and select \'OK\'.</li>Repeat this step until you get to the next dialog window.</li><li>Enter a name for the webfolder, then select \'Next\'.<li>Enter your login and password, and select \'OK\'.</li>Repeat this step until you get to the next dialog window.</li><li>Select \'Finish\'.</li><li>You can now access the webfolder from the start menu \'Computer\'.</li></ol><h3>Instructions for Windows XP with Internet Explorer 6</h3><ol><li>Start Internet Explorer 6</li><li>Choose the menu \'File &gt; Open...\'</li><li>In the \'Open\' field enter the following address:<br/><b>[WEBFOLDER_URI_IE]</b></li><li>Select the \'Open as web-folder\' option, then select \'OK\'.</li><li>Enter your login and password, and select \'OK\'.</li></ol><h3>Instructions for Windows XP without Internet Explorer 6</h3><ol><li>Double-click the \'My Network Places\' icon on the Desktop.</li><li>Click on \'Add Network Place\' from the \'Network Tasks\' menu on the left side of the window.</li><li>The \'Add a Network Place\' wizard will open, select \'Next\'.</li><li>Select the \'Choose Another Network Location\' option, and select \'Next\'.<li>In the \'Internet or Network Address\' field enter<br/><b>[WEBFOLDER_URI]</b><br/>then select \'Next\'.</li><li>Enter your login and password, and select \'OK\'.</li></ol>[/IF_WINDOWS][IF_MAC]<h3>Instructions for Mac OS X</h3><ol><li>Open the Finder</li><li>Choose the menu \'Go &gt; Connect to server...\'</li><li>In the address field enter the following address:<br/><b>[WEBFOLDER_URI]</b>, and select \'Connect\'.</li><li>Enter your login and password, and select \'OK\'.</li></ol>[/IF_MAC][IF_LINUX]<h3>Instructions for Linux with Konqueror</h3><ol><li>Start the Konqueror browser</li><li>In the address field enter the following address:<br/><b>[WEBFOLDER_URI_KONQUEROR]</b></li><li>Press the Enter key.</li><li>Enter your login and password, and select \'OK\'.</li></ol><h3>Instructions for Linux with Nautilus</h3><ol><li>Start the Nautilus browser</li><li>In the address field enter the following address:<br/><b>[WEBFOLDER_URI_NAUTILUS]</b></li><li>Press the Enter key.</li><li>Enter your login and password, and select \'OK\'.</li></ol>[/IF_LINUX]<p>If opening the webfolder does not work, please contact <a href=\\\"mailto:[ADMIN_MAIL]\\\">[ADMIN_MAIL]</a>.</p>');
